import React, { Component } from "react";
import Joi from "joi-browser";
import Form from "./common/form";
import { getMovies } from "../services/fakeMovieService";

class MovieForm extends Form {
  state = {
    data: { title: "", genre: "", numberInStock: "", dailyRentalRate: "" },
    errors: {},
  };

  componentDidMount() {
    const { match } = this.props;
    const movies = getMovies();
    const movie = movies.filter((m) => m._id === match.params.id);
    console.log(movie);
    const data = {
      title: movie[0].title,
      genre: movie[0].genre.name,
      numberInStock: movie[0].numberInStock,
      dailyRentalRate: movie[0].dailyRentalRate,
    };
    this.setState({ data });
  }

  schema = {
    title: Joi.string().required().label("Title"),
    genre: Joi.string().required().label("Genre"),
    numberInStock: Joi.number().required().label("Stock"),
    dailyRentalRate: Joi.number().required().label("Rate"),
  };

  doSubmit = () => {
    // Call Server
    console.log("Submitted");
  };

  render() {
    const { match, history } = this.props;
    return (
      <div>
        <h1>Movie Form {match.params.id}</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("title", "Title")}
          {this.renderInput("genre", "Genre")}
          {this.renderInput("numberInStock", "Stock", "number")}
          {this.renderInput("dailyRentalRate", "Rental")}
          {this.renderButton("Save")}
        </form>
        {/* <button
          className='btn btn-primary'
          onClick={() => history.push("/movies")}
        >
          Save
        </button> */}
      </div>
    );
  }
}

export default MovieForm;
