import React, { Component } from "react";
import { getGenres } from "../services/fakeGenreService";

const GenresList = (props) => {
  const genres = getGenres();
  return (
    <ul className='list-group d-inline-block'>
      <li
        onClick={props.allGenreSelection}
        className={
          props.currentGenre === null
            ? "list-group-item active"
            : "list-group-item"
        }
      >
        All Genres
      </li>
      {genres.map((genre) => (
        <li
          key={genre._id}
          onClick={() => props.genreSelection(genre)}
          className={
            props.currentGenre === genre.name
              ? "list-group-item active"
              : "list-group-item"
          }
        >
          {genre.name}
        </li>
      ))}
    </ul>
  );
};

export default GenresList;
