import React, { Component } from "react";
import Like from "./common/like";

class MoviesTable extends Component {
  raiseSort = (path) => {
    const sortColumn = { ...this.props.state.sortColumn };
    if (sortColumn.path === path)
      sortColumn.order = sortColumn.order === "asc" ? "desc" : "asc";
    else {
      sortColumn.path = path;
      sortColumn.order = "asc";
    }
    this.props.onSort(sortColumn);
  };
  render() {
    const { movies, onDelete, onLike, onSort } = this.props;
    return (
      <table className='table d-inline'>
        <thead>
          <tr>
            <th onClick={() => raiseSort("title")}>Title</th>
            <th onClick={() => raiseSort("genre.name")}>Genre</th>
            <th onClick={() => raiseSort("numberInStock")}>Stock</th>
            <th onClick={() => raiseSort("dailyRentalRate")}>Rate</th>
            <th></th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {movies.map((m) => (
            <tr key={m._id}>
              <td>{m.title}</td>
              <td>{m.genre.name}</td>
              <td>{m.numberInStock}</td>
              <td>{m.dailyRentalRate}</td>
              <td>
                <Like onLike={() => onLike(m)} liked={m.liked} />
              </td>
              <td>
                <button
                  onClick={() => onDelete(m)}
                  className='btn btn-danger btn-sm'
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  }
}

export default MoviesTable;
