import React, { Component, useState } from "react";
import { getMovies } from "../services/fakeMovieService";
import Like from "./common/like";
import Pagination from "./common/pagination";
import { paginate } from "../utils/paginate";
import GenresList from "./genres-list";

class Movies extends Component {
  state = {
    movies: getMovies(),
    pageSize: 4,
    currentPage: 1,
    currentGenre: null,
  };
  handleDelete = (m) => {
    const movies = this.state.movies.filter((film) => film !== m);
    this.setState({ movies: movies });
  };
  handleLike = (m) => {
    const movies = [...this.state.movies];
    const index = movies.indexOf(m);
    movies[index] = { ...movies[index] };
    movies[index].liked = !movies[index].liked;
    this.setState({ movies });
  };
  handlePageChange = (p) => {
    this.setState({ currentPage: p });
  };
  handleGenreSelection = (genre) => {
    const currentGenre = genre.name;
    const allMovies = getMovies();
    const movies = [...allMovies].filter(
      (movie) => movie.genre.name === genre.name
    );

    this.setState({ movies, currentGenre });
  };
  handleAllGenreSelection = () => {
    const currentGenre = null;
    const movies = getMovies();
    this.setState({ movies, currentGenre });
  };
  render() {
    const { length: count } = this.state.movies;
    const {
      pageSize,
      currentPage,
      movies: allMovies,
      currentGenre,
    } = this.state;

    if (count === 0) return <p>There are no movies to show</p>;

    const movies = paginate(allMovies, currentPage, pageSize);

    return (
      <React.Fragment>
        <GenresList
          currentGenre={currentGenre}
          genreSelection={this.handleGenreSelection}
          allGenreSelection={this.handleAllGenreSelection}
        />
        <p>Showing {count} movies in the databse</p>
        <table className='table d-inline'>
          <thead>
            <tr>
              <th>Title</th>
              <th>Genre</th>
              <th>Stock</th>
              <th>Rate</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {movies.map((m) => (
              <tr key={m._id}>
                <td>{m.title}</td>
                <td>{m.genre.name}</td>
                <td>{m.numberInStock}</td>
                <td>{m.dailyRentalRate}</td>
                <td>
                  <Like onLike={() => this.handleLike(m)} liked={m.liked} />
                </td>
                <td>
                  <button
                    onClick={() => this.handleDelete(m)}
                    className='btn btn-danger btn-sm'
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <Pagination
          itemsCount={count}
          pageSize={pageSize}
          currentPage={currentPage}
          onPageChange={this.handlePageChange}
        />
      </React.Fragment>
    );
  }
}

export default Movies;
