import React, { Component, useState } from "react";
import { getMovies } from "../services/fakeMovieService";
import Like from "./like";

class Movies extends Component {
  state = {
    movies: getMovies(),
  };
  handleDelete = (m) => {
    const movies = this.state.movies.filter((film) => film !== m);
    this.setState({ movies: movies });
  };
  handleLike = (m) => {
    console.log("Liked");
    const movies = [...this.state.movies];
    if (!m.liked) {
      const movie = movies.filter((movie) => movie._id === m.id);
      movie.liked = true;
      this.setState({ movies });
    }
    // if (m.liked) m.liked = false;
  };
  render() {
    const { length: count } = this.state.movies;

    if (count === 0) return <p>There are no movies to show</p>;
    return (
      <React.Fragment>
        <p>Showing {count} movies in the databse</p>
        <table className='table'>
          <thead>
            <tr>
              <th>Title</th>
              <th>Genre</th>
              <th>Stock</th>
              <th>Rate</th>
              <th></th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {this.state.movies.map((m) => (
              <tr key={m._id}>
                <td>{m.title}</td>
                <td>{m.genre.name}</td>
                <td>{m.numberInStock}</td>
                <td>{m.dailyRentalRate}</td>
                <td>
                  <Like onLike={() => this.handleLike(m)} liked={m.liked} />
                </td>
                <td>
                  <button
                    onClick={() => this.handleDelete(m)}
                    className='btn btn-danger btn-sm'
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </React.Fragment>
    );
  }
}

export default Movies;
